console.log("✅ Arquivo registro.js iniciando...");

document.addEventListener('DOMContentLoaded', () => {

    console.log("✅ Página HTML carregada! Adicionando funções aos botões.");

    const form = document.getElementById('registro-form');
    const localStorageKey = 'registroAtividadesData';

    // --- LÓGICA DE SALVAMENTO E CARREGAMENTO ---
    const salvarDados = () => {
        const dados = {};
        form.querySelectorAll('input[id]').forEach(campo => {
            dados[campo.id] = campo.value;
        });
        localStorage.setItem(localStorageKey, JSON.stringify(dados));
        console.log("Dados salvos no rascunho.");
    };

    const carregarDados = () => {
        const dadosSalvos = localStorage.getItem(localStorageKey);
        if (dadosSalvos) {
            const dados = JSON.parse(dadosSalvos);
            for (const idCampo in dados) {
                const campo = document.getElementById(idCampo);
                if (campo) {
                    campo.value = dados[idCampo];
                }
            }
            console.log("Rascunho carregado.");
        }
    };

    form.addEventListener('input', salvarDados);
    carregarDados();

    // --- LÓGICA PARA ADICIONAR NOVAS LINHAS DINAMICAMENTE ---
    document.querySelectorAll('.add-row-button').forEach(button => {
        button.addEventListener('click', (event) => {
            const containerId = event.target.dataset.target;
            const container = document.getElementById(containerId);
            if (container) {
                const firstRow = container.querySelector('.form-row');
                if (container.children.length > 0 && container.querySelector('.form-row')) {
                    const divisor = document.createElement('hr');
                    divisor.style.borderColor = 'rgba(255, 255, 255, 0.2)';
                    divisor.style.marginTop = '15px';
                    divisor.style.marginBottom = '15px';
                    container.appendChild(divisor);
                }
                const newRow = firstRow.cloneNode(true);
                newRow.querySelectorAll('input').forEach(input => input.value = '');
                container.appendChild(newRow);
            }
        });
    });

    // --- LÓGICA PARA GERAR O PDF AO ENVIAR O FORMULÁRIO ---
    form.addEventListener('submit', (event) => {
        event.preventDefault();

        try {
            console.log("Iniciando geração de PDF...");

            // --- 1. CAPTURA DOS DADOS DO FORMULÁRIO ---
            const unidade = document.getElementById('unidade').value;
            const responsavel = document.getElementById('responsavel').value;
            const dataInput = document.getElementById('data').value;
            let dataFormatada = '';
            if (dataInput) {
                const [ano, mes, dia] = dataInput.split('-');
                dataFormatada = `${dia}/${mes}/${ano}`;
            }
            const pontoFocalChegada = document.getElementById('ponto-focal-chegada').value;
            const horarioChegada = document.getElementById('horario-chegada').value;
            const horarioSaida = document.getElementById('horario-saida').value;

            const { jsPDF } = window.jspdf;
            const doc = new jsPDF();
            let finalY = 0;

  // --- 2. CRIAÇÃO DA TABELA ---

            const libertyHealthLogoBase64 = "data:image/png;base64,";

            doc.autoTable({

                startY: 10,

                theme: 'grid',

                body: [

                    // Linha 1: Logo e Título Principal

                    [

                        { content: '', rowSpan: 3, styles: { minCellWidth: 55 } }, // Célula do Logo, ocupa 3 linhas

                        { content: 'Registro De Atividades In Loco', colSpan: 5, styles: { fontStyle: 'bold', fontSize: 16, valign: 'middle', minCellHeight: 10 } },

                    ],

                    // Linha 2: Campo "Unidade"

                    [

                        { content: `Unidade: ${unidade}`, colSpan: 5, styles: { fontStyle: 'bold', valign: 'middle' } }

                    ],

                    // Linha 3: Campo "Responsável" e "Data"

                    [

                        { content: `Técnico responsável: ${responsavel}`, colSpan: 4, styles: { fontStyle: 'bold', valign: 'middle' } },

                        { content: `Data: ${dataFormatada}`, colSpan: 1, styles: { fontStyle: 'bold', halign: 'left', valign: 'middle' } }

                    ],

                    // Linha 4: Título da Seção de Ronda

                    [

                        { content: 'Responsável', colSpan: 6, styles: { halign: 'center', fontStyle: 'bold', fontSize: 12, fillColor: [220, 220, 220] } }

                    ],

                    // Linha 5: Ponto Focal de Chegada

                    [

                        { content: 'Ponto focal responsável', rowSpan: 2, styles: { fontStyle: 'bold', valign: 'middle' } },

                        { content: pontoFocalChegada, rowSpan: 2 , styles: { valign: 'middle', minCellWidth: 30 } },

                        { content: 'Chegada:', styles: { fontStyle: 'bold', valign: 'middle' } },

                        { content: horarioChegada, styles: { valign: 'middle' } },

                        { content: 'Assinatura:', rowSpan: 2, styles: { fontStyle: 'bold', valign: 'middle' } },

                        { content: '', rowSpan: 2, styles: { valign: 'middle' } },

                    ],

                    // Linha 6: Ponto Focal de Saída

                    [


                        

                        { content: 'Saída:', styles: { fontStyle: 'bold', valign: 'middle' } },

                        { content: horarioSaida, rowSpan: 2,  styles: { valign: 'middle' } },

                    ],

                ],

                didDrawCell: function (data) {

                    // Desenha o LOGO

                    if (data.section === 'body' && data.row.index === 0 && data.column.index === 0) {

                        if (libertyHealthLogoBase64.startsWith('data:image')) {

                            const logoLargura = 50;

                            const logoAltura = (logoLargura / 2.6);

                            doc.addImage(libertyHealthLogoBase64, 'PNG', data.cell.x + 2, data.cell.y + 5, logoLargura, logoAltura);

                        }

                    }

                }

            });

            

            finalY = doc.lastAutoTable.finalY + 10;


            // --- 3. CRIAÇÃO DAS OUTRAS TABELAS ---

            doc.setFontSize(12);

            doc.setFont("helvetica", "bold");

            const pageCenter = doc.internal.pageSize.getWidth() / 2;


            const getRowsData = (containerId, fields) => {

                const rows = document.querySelectorAll(`#${containerId} .form-row`);

                const data = [];

                rows.forEach(row => {

                    const rowData = fields.map(field => {

                        const input = row.querySelector(`input[name="${field}"]`);

                        return input ? input.value : '';

                    });

                    if (rowData.some(cell => cell.trim() !== '')) {

                        data.push(rowData);

                    }

                });

                return data;

            }; 

            const prioridadesData = getRowsData('prioridades-container', ['prioridade_descricao', 'prioridade_status', 'prioridade_card']);
            if (prioridadesData.length > 0) {
                doc.text('Prioridades da Unidade', pageCenter, finalY, { align: 'center' });
                finalY += 7;
                doc.autoTable({
                    startY: finalY,
                    head: [['Descrição', 'Status', 'Card', 'Assinatura']],
                    body: prioridadesData,
                    theme: 'striped',
                    headStyles: { fillColor: [44, 62, 80] },
                });
                finalY = doc.lastAutoTable.finalY + 10;
            }

            const chamadosData = getRowsData('chamados-container', ['chamado_glpi', 'chamado_descricao', 'chamado_status']);
            if (chamadosData.length > 0) {
                doc.text('Chamados da Unidade (GLPI/JIRA)', pageCenter, finalY, { align: 'center' });
                finalY += 7;
                doc.autoTable({
                    startY: finalY,
                    head: [['GLPI/JIRA', 'Descrição', 'Status', 'Assinatura']],
                    body: chamadosData,
                    theme: 'striped',
                    headStyles: { fillColor: [44, 62, 80] },
                });
                finalY = doc.lastAutoTable.finalY + 10;
            }
            
            const recebidosData = getRowsData('chamados-recebidos-container', ['recebido_descricao', 'recebido_status', 'recebido_card']);
            if (recebidosData.length > 0) {
                doc.text('Chamados Recebidos', pageCenter, finalY, { align: 'center' });
                finalY += 7;
                doc.autoTable({
                    startY: finalY,
                    head: [['Descrição', 'Status', 'Card', 'Assinatura']],
                    body: recebidosData,
                    theme: 'striped',
                    headStyles: { fillColor: [44, 62, 80] },
                });
                finalY = doc.lastAutoTable.finalY + 10;
            }

            const paineisData = getRowsData('paineis-container', ['painel_modelo', 'painel_localizacao', 'painel_status', 'painel_anydesk', 'painel_chamador']);
            if (paineisData.length > 0) {
                doc.text('Painéis', pageCenter, finalY, { align: 'center' });
                finalY += 7;
                doc.autoTable({
                    startY: finalY,
                    head: [['Modelo', 'Localização', 'Status', 'Anydesk', 'Local Chamador']],
                    body: paineisData,
                    theme: 'striped',
                    headStyles: { fillColor: [44, 62, 80] },
                });
                finalY = doc.lastAutoTable.finalY;
            }
            
            // --- 4. SALVAMENTO DO ARQUIVO E LIMPEZA ---
            const fileName = `Registro_${unidade.replace(/ /g, '_')}_${(dataFormatada || 'sem_data').replace(/\//g, '-')}.pdf`;
            doc.save(fileName);
            console.log(`PDF "${fileName}" gerado com sucesso.`);

            // Limpa o formulário e os dados salvos
            form.reset();
            localStorage.removeItem(localStorageKey);
            alert("PDF gerado com sucesso! O formulário foi limpo para o próximo registro.");

        } catch (error) {
            console.error("### ERRO AO GERAR O PDF ###", error);
            alert("Ocorreu um erro ao gerar o PDF. Verifique o console (F12) para ver os detalhes do erro.");
        }
    });
});